insert into disease (id, version, name, description) values ('8bfaae1d-2942-420f-a9e2-a514e99a43f8', 16, 'Astigmatism', ' Astigmatism is an imperfection in the curvature of your cornea');
insert into disease (id, version, name, description) values ('04487739-47b4-46de-a8c3-4e2207972d22', 1, 'Age-Related Macular Degeneration', ' Age-related macular degeneration is an eye condition that leads to the deterioration of the center of the retina');
insert into disease (id, version, name, description) values ('34856fe2-e0a3-4383-9375-6c1e49cd561b', 19, 'Blepharitis', ' Blepharitis is inflammation (swelling) of the eyelids');
insert into disease (id, version, name, description) values ('ddbe2a43-ae02-475d-9cb5-5db08cd13d6e', 12, 'Bell''s Palsy', ' Bell''s palsy is a temporary condition that causes certain muscles in your face to weaken or become paralyzed.');
insert into disease (id, version, name, description) values ('5a970b09-fd49-443e-bea2-3d51d311d07b', 12, 'Bacterial Keratitis', ' Bacterial keratitis is an infection of the cornea often due to improper care and cleaning of contact lenses or from injury to the cornea.');
insert into disease (id, version, name, description) values ('328c752e-e33e-402b-be98-929ec6a103b6', 19, 'Coloboma', ' A coloboma describes conditions where normal tissue in or around the eye is missing from birth.');
insert into disease (id, version, name, description) values ('87738859-3aff-452e-88cc-2188ff3fb0cf', 18, 'Color Blindness', ' Color blindness occurs when you are unable to see colors in a normal way');
insert into disease (id, version, name, description) values ('c9949d81-ded3-4b52-afff-eae6922547fd', 12, 'Corneal Abrasion', ' Corneal abrasion is a scratch or scrape on the eye’s cornea.');
insert into disease (id, version, name, description) values ('2e2ff5cc-52c4-4bdf-99be-876124224dfa', 8, 'Corneal Ulcer', ' A corneal ulcer is an open sore on the cornea.');
insert into disease (id, version, name, description) values ('e9116f02-9684-45cc-b9c2-ba6605d71683', 11, 'Eye Cancer', ' Cancer of the eye is a malignancy that starts and grows in your eye. A malignancy is a group of cells that are cancerous and that can spread to other sites in the body or invade and destroy tissues.');
insert into disease (id, version, name, description) values ('0ff9ba1e-7d77-4a37-b364-b26ce42ee4d5', 8, 'Floaters and Flashes', ' Floaters are shadows cast on the eye’s retina from clumps of gel or cells inside the vitreous. Flashes are flashes of light that you see when the vitreous gel pulls or rubs the retina.');
insert into disease (id, version, name, description) values ('6b21368a-96f9-4f4d-a277-a7066ced79a9', 5, 'Glaucoma', ' Glaucoma is a disease that damages the eye’s optic nerve.');
insert into disease (id, version, name, description) values ('26b5ef69-9f1e-4a72-9bb6-80724fbeb34d', 2, 'Graves'' Disease', ' Graves disease is an autoimmune disorder that leads to over activity of the thyroid gland. ');
insert into disease (id, version, name, description) values ('559b2774-b807-4e5a-80b4-89955e2a0699', 5, 'Hyphema', ' A hyphema is a collection of blood in the front part of the eye.');
insert into disease (id, version, name, description) values ('e0726188-035c-406a-a000-b29139b25824', 4, 'Keratitis', ' Keratitis is a condition where the cornea becomes swollen or inflamed making the eye red and painful and affecting vision.');
insert into disease (id, version, name, description) values ('c34d967f-cc21-44f5-9308-085cfb495702', 14, 'Low Vision', ' Low vision is a loss of eyesight that makes everyday tasks difficult or impossible');
insert into disease (id, version, name, description) values ('f5c6dfd7-b159-42eb-bc8f-1bd0d39ea281', 11, 'Amblyopia (Lazy Eye)', ' Lazy eye is poor vision in an eye that did not develop normal sight during childhood. Lazy eye is also called amblyopia.');
insert into disease (id, version, name, description) values ('6c200f2d-714e-4f6f-8e43-ae668014d630', 3, 'Macular Edema', ' Macular edema is a swelling or thickening of the macula; the area of the retina responsible for central vision.');
insert into disease (id, version, name, description) values ('957664e9-5f3f-4aa1-8aa8-87b9e6938b32', 3, 'Myopia (Nearsightedness)', ' Myopia (nearsightedness) is a refractive error which means the eye does not bend or refract light properly. ');
