insert into disease (id, version, name, description) values ('79850408-38aa-4f93-8a71-eaade589f016', 0, 'Ocular Hypertension', ' Ocular hypertension is when the pressure inside the eye (intraocular pressure) is higher than normal');
insert into disease (id, version, name, description) values ('82082e0e-5bdc-4d47-9e4a-dc7534a6cf66', 18, 'Ocular Melanoma', ' Ocular melanoma (also called eye melanoma) is a type of cancer that develops in the cells that produce pigment ');
insert into disease (id, version, name, description) values ('be0904c3-7473-4a69-b431-31d16a374f77', 6, 'Optic Neuritis', ' Optic neuritis is an inflammation of the eye’s optic nerve.');
insert into disease (id, version, name, description) values ('11ff7a9e-8931-48a2-a88d-3b3b6274cbac', 8, 'Presbyopia', ' Presbyopia is an age-related condition when the eye’s lens doesn’t change shape as easily as it once did');
insert into disease (id, version, name, description) values ('7656e260-2bfe-4f27-b032-3468efc0d931', 12, 'Retinoblastoma', ' Retinoblastoma is cancer of the eye that begins in the retina and is most common among children.');
insert into disease (id, version, name, description) values ('6a6492e5-f572-4823-b191-9a416df80088', 15, 'Scleritis', ' Scleritis is a painful swelling of the white part of the eye which is also known as the sclera');
insert into disease (id, version, name, description) values ('40e434f9-1b65-4434-85fb-69f58223b503', 9, 'Strabismus', ' Strabismus is a visual problem in which the eyes are not aligned properly and point in different directions.');
insert into disease (id, version, name, description) values ('c91ead0b-d713-473d-af98-57d33dae18d3', 2, 'Trichiasis', ' Trichiasis is a common eyelid abnormality in which the eyelashes are misdirected and grow inwards toward the eye');
insert into disease (id, version, name, description) values ('79b67e54-3fbf-45f8-812a-56317ed7517a', 12, 'Trachoma', ' An eye infection affecting both eyes');
insert into disease (id, version, name, description) values ('45bb7fa1-c2cf-4b57-a5a3-ac9539b714fd', 7, 'Usher Syndrome', ' Usher syndrome is the most common genetic condition that affects both vision and hearing. The major symptoms of Usher syndrome are hearing loss and vision loss from an eye disorder called retinitis pigmentosa or RP.');
